async function generateDiseases() {
    console.log("Connecting to DB...");
    const connection = await mysql.createConnection(dbConfig);

    let values = diseaseData.map(([name, category, description]) => [
        name,
        category,
        description
    ]);

    console.log(`Inserting ${values.length} diseases...`);

    const sql = `
        INSERT INTO diseases
        (diseaseName, category, description)
        VALUES ?
    `;

    await connection.query(sql, [values]);

    console.log(`âœ“ ${values.length} diseases mock data inserted successfully!`);
    
    // Show category distribution
    const categoryCounts = {};
    diseaseData.forEach(d => {
        categoryCounts[d[1]] = (categoryCounts[d[1]] || 0) + 1;
    });
    
    console.log("\nCategory Distribution:");
    for (const [cat, count] of Object.entries(categoryCounts)) {
        console.log(`  ${cat}: ${count}`);
    }

    await connection.end();
}

generateDiseases().catch(console.error);